var showRel = 0;
var	resetScrollEle = function() 
{
	var a = $("#cate_lev2_div_" + showRel + ">li").find("a").eq(0).width() + "px";
	$(".cate_icon_img").css(
	{
		width: a,
		height: a
	})
};
	
window.onload = function() 
{
	$(".search_header .menu").click(function(a) 
	{
		$(this).hasClass("active") ? ($(this).removeClass("active"), $(".t-nav").removeClass("active"), setTimeout(function() 
		{
			$(".main_box").css("height", "");
			lv1S.refresh();
			lv2S.refresh()
		}, 300)) 
		: 
		($(this).addClass("active"), $(".t-nav").addClass("active"), setTimeout(function() 
		{
			var a = $(".t-nav").height();
			var	b = $(".main_box").height();
			$(".main_box").css("height", b - a + "px");
			lv1S.refresh();
			lv2S.refresh()
		}, 300));
		a.preventDefault();
	})
};

window.addEventListener("resize", function() 
{
	resetScrollEle()
});

window.addEventListener("orientationchange", function() 
{
	resetScrollEle()
});

//定义一些变量
var lv2S = null;
var nav  = $(".class_list_nav_box ul>a");
var	cSrc = function(a) 
{
	a.each(function(a, b) 
	{
		var c = $(b).attr("data-src"),
			d = $(b).attr("src");
		    c !== d && $(b).attr("src", c)
	})
};

var lv1S = new iScroll("class_list_nav_box",
{
	hScrollbar: !1,
	vScrollbar: !1,
	hScroll: !1,
	vScroll: !0
});

nav.bind("click", function() 
{
	var pid = $(this).find("input[type='hidden']").val();
	$(this).children("li").addClass("on");
	$(this).siblings().children("li").removeClass("on");
	var a = $(this).attr("rel");
	showRel = a;
	var index = a;
	a = $("#cate_lev2_div_" + a);
	a.show().siblings().hide();
	cSrc(a.find("img"));
	resetScrollEle();
	if(index != 0)
	{
		var g = $("#cate_lev2_div_"+index).find("section").length;
		if(g == 0)
		{
			getDatas(pid, index);
		}
	}
});
nav.eq(0).trigger("click");

